import React from 'react';
import { 
  Container, 
  Typography, 
  Box, 
  Grid,
  Paper,
  Button,
  useTheme,
  useMediaQuery,
  Fade,
  Grow
} from '@mui/material';
import { useLanguage } from '../contexts/LanguageContext';
import GradientBackground from '../components/common/GradientBackground';
import Icon, { ICONS } from '../components/common/Icon';

const AboutPage = () => {
  const { t } = useLanguage();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const features = [
    {
      icon: ICONS.SPEED,
      titleKey: 'about.features.fast',
      delay: 0
    },
    {
      icon: ICONS.DEVICES,
      titleKey: 'about.features.multiPlatform',
      delay: 100
    },
    {
      icon: ICONS.LANGUAGE,
      titleKey: 'about.features.multiLanguage',
      delay: 200
    }
  ];

  const stats = [
    {
      value: '12+',
      labelKey: 'about.stats.categories',
      icon: ICONS.CATEGORY,
      delay: 0
    },
    {
      value: '100+',
      labelKey: 'about.stats.units',
      icon: ICONS.CONVERT,
      delay: 100
    },
    {
      value: '3+',
      labelKey: 'about.stats.languages',
      icon: ICONS.LANGUAGE,
      delay: 200
    }
  ];

  const sections = [
    {
      icon: ICONS.TARGET,
      titleKey: 'about.welcome.title',
      contentKey: 'about.welcome.content',
      delay: 0
    },
    {
      icon: ICONS.COMPASS,
      titleKey: 'about.understanding.title',
      contentKey: 'about.understanding.content',
      delay: 100
    },
    {
      icon: ICONS.HEART,
      titleKey: 'about.commitment.title',
      contentKey: 'about.commitment.content',
      delay: 200
    },
    {
      icon: ICONS.TRENDING_UP,
      titleKey: 'about.improvement.title',
      contentKey: 'about.improvement.content',
      delay: 300
    }
  ];

  return (
    <Box>
      <GradientBackground
        variant="mesh"
        sx={{ 
          py: { xs: 6, md: 12 },
          position: 'relative',
          overflow: 'hidden'
        }}
      >
        <Container maxWidth="lg">
          <Fade in timeout={1000}>
            <Box>
              <Typography 
                variant="h1" 
                component="h1"
                sx={{ 
                  mb: 2,
                  textAlign: 'center',
                  fontSize: { xs: '2.5rem', sm: '3.5rem', md: '4rem' },
                  fontWeight: 800,
                  background: 'linear-gradient(45deg, #2563eb 30%, #4f46e5 90%)',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  letterSpacing: '-0.02em'
                }}
              >
                {t('about.title')}
              </Typography>
              <Typography
                variant="h5"
                component="p"
                color="text.secondary"
                sx={{
                  textAlign: 'center',
                  maxWidth: '800px',
                  mx: 'auto',
                  mb: 4,
                  lineHeight: 1.6
                }}
              >
                {t('about.subtitle')}
              </Typography>
            </Box>
          </Fade>
        </Container>
      </GradientBackground>

      <Container maxWidth="lg" sx={{ py: { xs: 6, md: 12 } }}>
        {/* Main Content Sections */}
        <Box sx={{ mb: 8 }}>
          <Grid container spacing={4}>
            {sections.map((section, index) => (
              <Grid item xs={12} key={index}>
                <Fade in timeout={1000} style={{ transitionDelay: `${section.delay}ms` }}>
                  <Paper
                    elevation={0}
                    sx={{
                      p: 4,
                      borderRadius: 3,
                      backgroundColor: 'background.paper',
                      transition: 'transform 0.2s ease-in-out, box-shadow 0.2s ease-in-out',
                      '&:hover': {
                        transform: 'translateY(-4px)',
                        boxShadow: theme.shadows[4]
                      }
                    }}
                  >
                    <Box sx={{ display: 'flex', alignItems: 'flex-start', gap: 3 }}>
                      <Icon
                        name={section.icon}
                        size={40}
                        color="primary"
                        animate="pulse"
                      />
                      <Box>
                        <Typography
                          variant="h4"
                          component="h2"
                          sx={{
                            mb: 2,
                            fontWeight: 700,
                            fontSize: { xs: '1.5rem', sm: '1.75rem' }
                          }}
                        >
                          {t(section.titleKey)}
                        </Typography>
                        <Typography
                          variant="body1"
                          color="text.secondary"
                          sx={{ lineHeight: 1.7 }}
                        >
                          {t(section.contentKey)}
                        </Typography>
                      </Box>
                    </Box>
                  </Paper>
                </Fade>
              </Grid>
            ))}
          </Grid>
        </Box>

        {/* Features Section */}
        <Typography
          variant="h3"
          component="h2"
          sx={{
            mb: 4,
            textAlign: 'center',
            fontWeight: 700
          }}
        >
          {t('about.features.title')}
        </Typography>
        <Grid container spacing={4}>
          {features.map((feature, index) => (
            <Grid item xs={12} md={4} key={index}>
              <Grow in timeout={1000} style={{ transitionDelay: `${feature.delay}ms` }}>
                <Paper 
                  elevation={0}
                  sx={{ 
                    p: 4, 
                    height: '100%',
                    textAlign: 'center',
                    backgroundColor: 'background.paper',
                    borderRadius: 3,
                    transition: 'transform 0.2s ease-in-out, box-shadow 0.2s ease-in-out',
                    '&:hover': {
                      transform: 'translateY(-4px)',
                      boxShadow: theme.shadows[4]
                    }
                  }}
                >
                  <Icon 
                    name={feature.icon}
                    size={56}
                    color="primary"
                    sx={{ mb: 3 }}
                    animate="pulse"
                  />
                  <Typography 
                    variant="h5" 
                    component="h3" 
                    sx={{ 
                      mb: 2,
                      fontWeight: 600
                    }}
                  >
                    {t(feature.titleKey)}
                  </Typography>
                  <Typography 
                    variant="body1" 
                    color="text.secondary"
                    sx={{ lineHeight: 1.7 }}
                  >
                    {t(`${feature.titleKey}Description`)}
                  </Typography>
                </Paper>
              </Grow>
            </Grid>
          ))}
        </Grid>

        {/* Stats Section */}
        <Box
          sx={{
            mt: 8,
            p: 4,
            borderRadius: 3,
            backgroundColor: 'background.paper',
            boxShadow: theme.shadows[1]
          }}
        >
          <Grid container spacing={4}>
            {stats.map((stat, index) => (
              <Grid item xs={12} md={4} key={index}>
                <Fade in timeout={1000} style={{ transitionDelay: `${stat.delay}ms` }}>
                  <Box
                    sx={{
                      textAlign: 'center',
                      p: 2
                    }}
                  >
                    <Icon
                      name={stat.icon}
                      size={48}
                      color="primary"
                      sx={{ mb: 2 }}
                      animate="pulse"
                    />
                    <Typography
                      variant="h3"
                      sx={{
                        mb: 1,
                        fontWeight: 700,
                        background: 'linear-gradient(45deg, #2563eb, #4f46e5)',
                        WebkitBackgroundClip: 'text',
                        WebkitTextFillColor: 'transparent'
                      }}
                    >
                      {stat.value}
                    </Typography>
                    <Typography
                      variant="h6"
                      color="text.secondary"
                    >
                      {t(stat.labelKey)}
                    </Typography>
                  </Box>
                </Fade>
              </Grid>
            ))}
          </Grid>
        </Box>

        <Box 
          sx={{ 
            mt: { xs: 8, md: 12 }, 
            textAlign: 'center',
            p: { xs: 4, md: 6 },
            borderRadius: 3,
            background: theme.palette.mode === 'dark' 
              ? 'linear-gradient(to bottom right, rgba(37, 99, 235, 0.1), rgba(79, 70, 229, 0.1))'
              : 'linear-gradient(to bottom right, rgba(37, 99, 235, 0.05), rgba(79, 70, 229, 0.05))'
          }}
        >
          <Typography 
            variant="h3" 
            component="h2" 
            sx={{ 
              mb: 3,
              fontWeight: 700,
              background: 'linear-gradient(45deg, #2563eb, #4f46e5)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent'
            }}
          >
            {t('about.contact.title')}
          </Typography>
          <Typography 
            variant="h6" 
            component="p" 
            color="text.secondary"
            sx={{ mb: 4, maxWidth: '600px', mx: 'auto', lineHeight: 1.7 }}
          >
            {t('about.contact.description')}
          </Typography>
          <Box 
            sx={{ 
              display: 'flex', 
              gap: 2, 
              justifyContent: 'center',
              flexDirection: { xs: 'column', sm: 'row' }
            }}
          >
            <Button
              variant="contained"
              size="large"
              startIcon={<Icon name={ICONS.EMAIL} size={20} />}
              sx={{ 
                px: 4,
                py: 1.5,
                borderRadius: 2,
                textTransform: 'none',
                fontSize: '1.1rem'
              }}
            >
              {t('about.contact.sendMessage')}
            </Button>
            <Button
              variant="outlined"
              size="large"
              startIcon={<Icon name={ICONS.FEEDBACK} size={20} />}
              sx={{ 
                px: 4,
                py: 1.5,
                borderRadius: 2,
                textTransform: 'none',
                fontSize: '1.1rem'
              }}
            >
              {t('about.contact.submitFeedback')}
            </Button>
          </Box>
        </Box>
      </Container>
    </Box>
  );
};

export default AboutPage;
