import React, { useState } from 'react';
import { convertUnits } from '../services/api';

const ConversionForm = () => {
  const [formData, setFormData] = useState({
    category: 'length',
    fromUnit: 'm',
    toUnit: 'km',
    value: ''
  });
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setError(null);
      const response = await convertUnits(
        formData.category,
        formData.fromUnit,
        formData.toUnit,
        formData.value
      );
      setResult(response.result);
    } catch (err) {
      setError(err.message);
      setResult(null);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <select name="category" value={formData.category} onChange={handleChange}>
          <option value="length">Length</option>
          <option value="weight">Weight</option>
          <option value="temperature">Temperature</option>
        </select>

        <input
          type="number"
          name="value"
          value={formData.value}
          onChange={handleChange}
          placeholder="Enter value"
          required
        />

        <select name="fromUnit" value={formData.fromUnit} onChange={handleChange}>
          <option value="m">Meters</option>
          <option value="km">Kilometers</option>
          <option value="cm">Centimeters</option>
        </select>

        <select name="toUnit" value={formData.toUnit} onChange={handleChange}>
          <option value="m">Meters</option>
          <option value="km">Kilometers</option>
          <option value="cm">Centimeters</option>
        </select>

        <button type="submit">Convert</button>
      </form>

      {error && <div className="error">{error}</div>}
      {result && (
        <div className="result">
          <h3>Result: {result.formatted}</h3>
          <p>Formula: {result.formula}</p>
          <ul>
            {result.steps.map((step, index) => (
              <li key={index}>{step}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default ConversionForm;
