import React from 'react';
import { Paper, Typography, Grid, Button, useTheme } from '@mui/material';
import { commonConversions } from '../../data/categories';
import Icon from '../common/Icon';

const CommonConversions = ({ onSelect }) => {
  const theme = useTheme();

  return (
    <Paper 
      elevation={0}
      sx={{ 
        mt: 4, 
        p: 3,
        bgcolor: theme.palette.mode === 'dark' ? 'rgba(37, 99, 235, 0.05)' : 'rgba(37, 99, 235, 0.02)',
        borderRadius: 2,
        border: 1,
        borderColor: theme.palette.mode === 'dark' ? 'rgba(37, 99, 235, 0.1)' : 'rgba(37, 99, 235, 0.05)',
      }}
    >
      <Typography 
        variant="h6" 
        sx={{ 
          color: 'text.primary', 
          mb: 3,
          fontWeight: 600,
          fontSize: '1.125rem'
        }}
      >
        Common Conversions
      </Typography>
      <Grid container spacing={2}>
        {commonConversions.map((conversion) => (
          <Grid item xs={12} sm={6} md={4} key={conversion.id}>
            <Button
              variant="outlined"
              fullWidth
              sx={{
                justifyContent: 'flex-start',
                textTransform: 'none',
                textAlign: 'left',
                p: 2,
                borderRadius: 1,
                borderColor: theme.palette.mode === 'dark' ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.1)',
                bgcolor: theme.palette.mode === 'dark' ? 'rgba(255, 255, 255, 0.02)' : 'rgba(0, 0, 0, 0.02)',
                '&:hover': {
                  bgcolor: theme.palette.mode === 'dark' ? 'rgba(255, 255, 255, 0.05)' : 'rgba(0, 0, 0, 0.05)',
                  borderColor: theme.palette.mode === 'dark' ? 'rgba(255, 255, 255, 0.2)' : 'rgba(0, 0, 0, 0.2)',
                }
              }}
              onClick={() => onSelect(conversion)}
              startIcon={<Icon name={conversion.icon} size={20} />}
            >
              <div style={{ overflow: 'hidden', textOverflow: 'ellipsis' }}>
                <Typography 
                  variant="body2" 
                  sx={{ 
                    fontWeight: 500,
                    color: theme.palette.mode === 'dark' ? '#fff' : '#000'
                  }}
                >
                  {conversion.name}
                </Typography>
                <Typography 
                  variant="caption" 
                  sx={{ 
                    display: 'block',
                    color: theme.palette.mode === 'dark' ? 'rgba(255, 255, 255, 0.5)' : 'rgba(0, 0, 0, 0.5)'
                  }}
                >
                  {conversion.from} â†’ {conversion.to}
                </Typography>
              </div>
            </Button>
          </Grid>
        ))}
      </Grid>
    </Paper>
  );
};

export default CommonConversions;
