import React from 'react';
import { Paper, Typography, Grid, Button } from '@mui/material';
import { commonConversions } from '../../data/categories';

const CommonConversions = ({ onSelect }) => {
  return (
    <Paper sx={{ mt: 6, p: 4 }}>
      <Typography variant="h6" sx={{ color: 'text.primary', mb: 4 }}>
        Common Conversions
      </Typography>
      <Grid container spacing={2}>
        {commonConversions.map((conversion) => (
          <Grid item xs={12} sm={6} md={4} key={conversion.id}>
            <Button
              variant="outlined"
              fullWidth
              sx={{
                justifyContent: 'flex-start',
                textTransform: 'none',
                textAlign: 'left',
                p: 2
              }}
              onClick={() => onSelect(conversion)}
              startIcon={<span className="material-icons">{conversion.icon}</span>}
            >
              <div style={{ overflow: 'hidden', textOverflow: 'ellipsis' }}>
                <Typography variant="body2" sx={{ fontWeight: 500 }}>
                  {conversion.name}
                </Typography>
                <Typography variant="caption" color="textSecondary" sx={{ display: 'block' }}>
                  {conversion.from} â†’ {conversion.to}
                </Typography>
              </div>
            </Button>
          </Grid>
        ))}
      </Grid>
    </Paper>
  );
};

export default CommonConversions;
