import React, { useState, useEffect, lazy, Suspense } from 'react';
import { categories } from '../../data/categories';
import {
  Box,
  IconButton,
  Typography,
  useTheme,
  useMediaQuery,
  Tooltip,
  Alert,
  Collapse,
  Grid,
  CircularProgress
} from '@mui/material';
import { useLanguage } from '../../contexts/LanguageContext';
import { useNavigate, useLocation } from 'react-router-dom';
import SwapVertIcon from '@mui/icons-material/SwapVert';
import Button from '../common/Button';
import TextField from '../common/TextField';
import Select from '../common/Select';
import Paper from '../common/Paper';

// Lazy load non-critical components
const ConversionDetails = lazy(() => import('./ConversionDetails'));
const CommonConversions = lazy(() => import('./CommonConversions'));

// Loading fallback component
const LoadingFallback = () => (
  <Box sx={{ display: 'flex', justifyContent: 'center', p: 3 }}>
    <CircularProgress />
  </Box>
);

// Debounce function for performance
const debounce = (func, wait) => {
  let timeout;
  return function executedFunction(...args) {
    const later = () => {
      clearTimeout(timeout);
      func(...args);
    };
    clearTimeout(timeout);
    timeout = setTimeout(later, wait);
  };
};

// Update API base URL configuration
const API_BASE_URL = process.env.NODE_ENV === 'production'
  ? `${window.location.origin}/api`  // Use window.location.origin in production
  : 'http://localhost:5001/api';  // Use localhost in development

console.log('API Base URL:', API_BASE_URL); // Add logging for debugging

const ConverterForm = ({ category, onFormulaUpdate }) => {
  const { t } = useLanguage();
  const theme = useTheme();
  const navigate = useNavigate();
  const location = useLocation();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  
  const [fromValue, setFromValue] = useState('');
  const [toValue, setToValue] = useState('');
  const [fromUnit, setFromUnit] = useState('');
  const [toUnit, setToUnit] = useState('');
  const [error, setError] = useState('');
  const [conversionResult, setConversionResult] = useState(null);
  const [loading, setLoading] = useState(false);

  // Find category data case-insensitively
  const categoryData = categories.find(cat => 
    cat.id.toLowerCase() === category?.toLowerCase()
  );
  const availableUnits = categoryData?.commonUnits || [];

  // Debounced conversion handler
  const debouncedConvert = React.useCallback(
    debounce(() => {
      if (fromValue && fromUnit && toUnit && validateNumber(fromValue)) {
        handleConvert();
      }
    }, 300),
    [fromValue, fromUnit, toUnit]
  );

  useEffect(() => {
    debouncedConvert();
    return () => debouncedConvert.cancel?.();
  }, [fromValue, fromUnit, toUnit]);

  // Cache unit options
  const unitOptions = React.useMemo(() => 
    availableUnits.map(unit => ({
      value: unit,
      label: t(`units.${unit.toLowerCase()}`)
    })),
    [availableUnits, t]
  );

  useEffect(() => {
    // Handle navigation state for common conversions
    if (location.state?.fromUnit && location.state?.toUnit) {
      setFromUnit(location.state.fromUnit);
      setToUnit(location.state.toUnit);
      // Clear the navigation state
      navigate(location.pathname, { replace: true });
    }
    // If no state, set default units
    else if (availableUnits.length > 0 && !fromUnit && !toUnit) {
      setFromUnit(availableUnits[0]);
      setToUnit(availableUnits[1]);
    }
  }, [availableUnits, location.state, navigate, location.pathname]);

  const validateNumber = (value) => {
    if (!value || value.trim() === '') return false;
    const num = parseFloat(value);
    return !isNaN(num) && isFinite(num);
  };

  const handleConvert = async () => {
    if (!validateNumber(fromValue) || !fromUnit || !toUnit) {
      setError(t('errors.invalidNumber'));
      return;
    }

    if (!categoryData) {
      console.error('Category data not found:', category);
      setError(t('errors.conversionFailed'));
      return;
    }

    setError('');
    setLoading(true);

    try {
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 5000); // 5 second timeout

      const url = `${API_BASE_URL}/convert?category=${categoryData.id}&fromUnit=${fromUnit}&toUnit=${toUnit}&value=${fromValue}`;
      console.log('Making API request to:', url);

      const response = await fetch(url, { 
        signal: controller.signal,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        },
        credentials: 'same-origin'
      });

      clearTimeout(timeoutId);

      // Log the raw response for debugging
      console.log('Response status:', response.status);
      console.log('Response headers:', Object.fromEntries(response.headers.entries()));
      
      let data;
      const contentType = response.headers.get('content-type');
      const responseText = await response.text();
      
      console.log('Content-Type:', contentType);
      console.log('Raw response:', responseText);

      // Only try to parse as JSON if the content-type is application/json
      if (contentType && contentType.includes('application/json')) {
        try {
          data = JSON.parse(responseText);
        } catch (parseError) {
          console.error('Failed to parse response as JSON:', parseError);
          throw new Error(t('errors.invalidResponse'));
        }
      } else {
        console.error('Received non-JSON response:', contentType);
        throw new Error(t('errors.invalidResponse'));
      }
      
      if (!response.ok) {
        throw new Error(data.error || t('errors.conversionFailed'));
      }

      if (!data || !data.success || typeof data.result?.value === 'undefined') {
        console.error('Invalid response data:', data);
        throw new Error(t('errors.invalidResponse'));
      }

      const resultValue = parseFloat(data.result.value);
      if (isNaN(resultValue)) {
        throw new Error(t('errors.invalidNumber'));
      }

      setToValue(resultValue.toString());
      
      // Format the conversion result with proper units and validation
      const formattedResult = {
        from: {
          unit: fromUnit,
          value: parseFloat(fromValue),
          formatted: `${fromValue} ${t(`units.${fromUnit.toLowerCase()}`)}`
        },
        to: {
          unit: toUnit,
          value: resultValue,
          formatted: `${resultValue} ${t(`units.${toUnit.toLowerCase()}`)}`
        },
        formula: data.formula || undefined,
        steps: Array.isArray(data.steps) ? data.steps : undefined
      };
      
      setConversionResult(formattedResult);
      if (onFormulaUpdate && data.formula) {
        onFormulaUpdate(data.formula);
      }
      setError('');
    } catch (err) {
      console.error('Conversion error:', err);
      if (err.name === 'AbortError') {
        setError(t('errors.timeout'));
      } else if (!navigator.onLine) {
        setError(t('errors.offline'));
      } else {
        setError(err.message || t('errors.networkError'));
      }
      setToValue('');
      setConversionResult(null);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');

    if (!validateNumber(fromValue)) {
      setError(t('errors.invalidNumber'));
      return;
    }

    handleConvert();
  };

  const handleSwapUnits = () => {
    const oldFromValue = fromValue;
    const oldToValue = toValue;
    setFromUnit(toUnit);
    setToUnit(fromUnit);
    setFromValue(oldToValue);
    setToValue(oldFromValue);
  };

  const handleCommonConversionSelect = (conversion) => {
    if (conversion.category !== category) {
      // If the conversion is from a different category, navigate to that category
      navigate(`/convert/${conversion.category}`, { 
        state: { 
          fromUnit: conversion.from,
          toUnit: conversion.to
        }
      });
      return;
    }
    
    // If it's the same category, just update the units
    setFromUnit(conversion.from);
    setToUnit(conversion.to);
  };

  return (
    <Box 
      component="form" 
      onSubmit={handleSubmit} 
      sx={{ 
        width: '100%',
        maxWidth: 600,
        mx: 'auto'
      }}
      role="main"
      aria-label={t('converter.title')}
    >
      <Paper
        elevation={0}
        transparent={isMobile}
        sx={{
          p: { xs: 2, sm: 3 },
          borderRadius: 2,
          backgroundColor: theme.palette.background.paper,
          border: `1px solid ${theme.palette.divider}`,
          '@media (max-width: 600px)': {
            border: 'none',
            p: 2,
            backgroundColor: 'transparent'
          }
        }}
      >
        <Grid container spacing={3} alignItems="flex-start">
          <Grid item xs={12} sm={6}>
            <TextField
              label={t('converter.value')}
              value={fromValue}
              onChange={(e) => {
                const value = e.target.value;
                if (value === '' || /^-?\d*\.?\d*$/.test(value)) {
                  setFromValue(value);
                  setError('');
                }
              }}
              onKeyDown={(e) => {
                if (e.key === 'Enter') {
                  e.preventDefault();
                  handleSubmit(e);
                }
              }}
              error={!!error}
              helperText={error}
              type="text"
              inputMode="decimal"
              placeholder="0"
              aria-label={t('converter.value')}
              aria-invalid={!!error}
              aria-describedby={error ? 'value-error' : undefined}
              fullWidth
              autoFocus
            />
            {error && (
              <div id="value-error" role="alert" style={{ display: 'none' }}>
                {error}
              </div>
            )}
          </Grid>
          <Grid item xs={12} sm={6}>
            <Select
              label={t('converter.fromUnit')}
              value={fromUnit}
              onChange={(e) => setFromUnit(e.target.value)}
              options={unitOptions}
              size="medium"
              fullWidth
              aria-label={t('converter.fromUnit')}
              onKeyDown={(e) => {
                if (e.key === 'Enter') {
                  e.preventDefault();
                  handleSubmit(e);
                }
              }}
            />
          </Grid>
          <Grid 
            item 
            xs={12} 
            sx={{ 
              display: 'flex', 
              justifyContent: 'center',
              py: { xs: 1, sm: 1.5 }
            }}
          >
            <Tooltip title={t('converter.swap')} arrow>
              <IconButton
                onClick={handleSwapUnits}
                aria-label={t('converter.swap')}
                onKeyDown={(e) => {
                  if (e.key === 'Enter' || e.key === ' ') {
                    e.preventDefault();
                    handleSwapUnits();
                  }
                }}
                sx={{
                  p: 1.5,
                  width: 44,
                  height: 44,
                  border: `1px solid ${theme.palette.divider}`,
                  borderRadius: '50%',
                  backgroundColor: theme.palette.background.paper,
                  '&:hover': {
                    backgroundColor: theme.palette.action.hover
                  },
                  '&:active': {
                    backgroundColor: theme.palette.action.selected,
                    transform: 'scale(0.95)'
                  },
                  '&:focus-visible': {
                    outline: `2px solid ${theme.palette.primary.main}`,
                    outlineOffset: 2
                  }
                }}
              >
                <SwapVertIcon />
              </IconButton>
            </Tooltip>
          </Grid>
          <Grid item xs={12} sm={6}>
            <Select
              label={t('converter.toUnit')}
              value={toUnit}
              onChange={(e) => setToUnit(e.target.value)}
              options={unitOptions}
              size="medium"
              fullWidth
              aria-label={t('converter.toUnit')}
              onKeyDown={(e) => {
                if (e.key === 'Enter') {
                  e.preventDefault();
                  handleSubmit(e);
                }
              }}
            />
          </Grid>
          <Grid item xs={12}>
            <Button
              fullWidth
              variant="contained"
              onClick={handleSubmit}
              disabled={loading || !fromValue || !fromUnit || !toUnit || !validateNumber(fromValue)}
              loading={loading}
              size="large"
              type="submit"
              aria-label={t('converter.convert')}
              aria-busy={loading}
              sx={{
                '&:focus-visible': {
                  outline: `2px solid ${theme.palette.primary.main}`,
                  outlineOffset: 2
                }
              }}
            >
              {t('converter.convert')}
            </Button>
          </Grid>
        </Grid>
      </Paper>
      
      <Suspense fallback={<LoadingFallback />}>
        {conversionResult && (
          <ConversionDetails 
            result={conversionResult} 
            sx={{ 
              mt: 3,
              '@media (max-width: 600px)': {
                mx: 0
              }
            }} 
          />
        )}
        
        <CommonConversions
          onSelect={handleCommonConversionSelect}
          sx={{ 
            mt: 4,
            '@media (max-width: 600px)': {
              mx: 0
            }
          }}
        />
      </Suspense>
    </Box>
  );
};

export default ConverterForm;
