import React, { useState, useEffect } from 'react';
import { categories } from '../../data/categories';
import ConversionDetails from './ConversionDetails';
import CommonConversions from './CommonConversions';
import {
  Box,
  TextField,
  MenuItem,
  IconButton,
  Button,
  Paper,
  Typography,
  useTheme,
  useMediaQuery,
  InputAdornment,
  Tooltip,
  Alert,
  Collapse,
  Grid,
  FormControl,
  InputLabel,
  Select,
  CircularProgress
} from '@mui/material';
import { useLanguage } from '../../contexts/LanguageContext';
import { useNavigate, useLocation } from 'react-router-dom';
import SwapVertIcon from '@mui/icons-material/SwapVert';

const API_BASE_URL = process.env.NODE_ENV === 'production'
  ? '/api'  // In production, use relative path
  : 'http://localhost:5001/api';  // In development, use port 5001

const ConverterForm = ({ category, onFormulaUpdate }) => {
  const { t } = useLanguage();
  const theme = useTheme();
  const navigate = useNavigate();
  const location = useLocation();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  
  const [fromValue, setFromValue] = useState('');
  const [toValue, setToValue] = useState('');
  const [fromUnit, setFromUnit] = useState('');
  const [toUnit, setToUnit] = useState('');
  const [error, setError] = useState('');
  const [conversionResult, setConversionResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const categoryData = categories.find(cat => cat.id === category);
  const availableUnits = categoryData?.commonUnits || [];

  useEffect(() => {
    // Handle navigation state for common conversions
    if (location.state?.fromUnit && location.state?.toUnit) {
      setFromUnit(location.state.fromUnit);
      setToUnit(location.state.toUnit);
      // Clear the navigation state
      navigate(location.pathname, { replace: true });
    }
    // If no state, set default units
    else if (availableUnits.length > 0 && !fromUnit && !toUnit) {
      setFromUnit(availableUnits[0]);
      setToUnit(availableUnits[1]);
    }
  }, [availableUnits, location.state, navigate, location.pathname]);

  const handleConvert = async () => {
    if (!fromValue || !fromUnit || !toUnit) return;

    setError('');
    setLoading(true);

    try {
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 5000); // 5 second timeout

      const response = await fetch(
        `${API_BASE_URL}/convert?category=${category}&fromUnit=${fromUnit}&toUnit=${toUnit}&value=${fromValue}`,
        { signal: controller.signal }
      );

      clearTimeout(timeoutId);

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || t('errors.conversionFailed'));
      }

      const data = await response.json();
      if (!data || typeof data.result === 'undefined') {
        throw new Error(t('errors.invalidResponse'));
      }

      const resultValue = parseFloat(data.result);
      if (isNaN(resultValue)) {
        throw new Error(t('errors.invalidNumber'));
      }

      setToValue(resultValue.toString());
      
      // Format the conversion result with proper units and validation
      const formattedResult = {
        from: {
          unit: fromUnit,
          value: parseFloat(fromValue),
          formatted: `${fromValue} ${fromUnit}`
        },
        to: {
          unit: toUnit,
          value: resultValue,
          formatted: `${resultValue} ${toUnit}`
        },
        formula: data.formula || undefined,
        steps: Array.isArray(data.steps) ? data.steps : undefined
      };
      
      setConversionResult(formattedResult);
      if (onFormulaUpdate && data.formula) {
        onFormulaUpdate(data.formula);
      }
      setError('');
    } catch (err) {
      console.error('Conversion error:', err);
      if (err.name === 'AbortError') {
        setError(t('errors.timeout'));
      } else if (!navigator.onLine) {
        setError(t('errors.offline'));
      } else {
        setError(err.message || t('errors.networkError'));
      }
      setToValue('');
      setConversionResult(null);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (fromValue && fromUnit && toUnit) {
      handleConvert();
    }
  }, [fromValue, fromUnit, toUnit]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');

    if (!fromValue.trim()) {
      setError(t('errors.required'));
      return;
    }

    if (isNaN(fromValue)) {
      setError(t('errors.invalidNumber'));
      return;
    }

    handleConvert();
  };

  const handleSwapUnits = () => {
    const oldFromValue = fromValue;
    const oldToValue = toValue;
    setFromUnit(toUnit);
    setToUnit(fromUnit);
    setFromValue(oldToValue);
    setToValue(oldFromValue);
  };

  const handleCommonConversionSelect = (conversion) => {
    if (conversion.category !== category) {
      // If the conversion is from a different category, navigate to that category
      navigate(`/convert/${conversion.category}`, { 
        state: { 
          fromUnit: conversion.from,
          toUnit: conversion.to
        }
      });
      return;
    }
    
    // If it's the same category, just update the units
    setFromUnit(conversion.from);
    setToUnit(conversion.to);
  };

  return (
    <Box sx={{ width: '100%' }}>
      <Paper
        elevation={0}
        sx={{
          p: { xs: 2, sm: 3 },
          borderRadius: 2,
          backgroundColor: theme.palette.background.paper,
          border: `1px solid ${theme.palette.divider}`,
          '@media (max-width: 600px)': {
            border: 'none',
            p: 2,
            backgroundColor: 'transparent'
          }
        }}
      >
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label={t('converter.value')}
              value={fromValue}
              onChange={(e) => setFromValue(e.target.value)}
              error={!!error}
              helperText={error}
              type="number"
              inputProps={{
                inputMode: 'decimal',
                pattern: '[0-9]*',
                step: 'any'
              }}
              sx={{
                '& .MuiInputBase-input': {
                  fontSize: '1rem',
                  py: 1.5
                }
              }}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth>
              <InputLabel>{t('converter.fromUnit')}</InputLabel>
              <Select
                value={fromUnit}
                onChange={(e) => setFromUnit(e.target.value)}
                label={t('converter.fromUnit')}
                sx={{
                  '& .MuiSelect-select': {
                    py: 1.5
                  }
                }}
              >
                {availableUnits.map((unit) => (
                  <MenuItem key={unit} value={unit}>
                    {t(`units.${unit}`)}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid 
            item 
            xs={12} 
            sx={{ 
              display: 'flex', 
              justifyContent: 'center',
              py: { xs: 1, sm: 1.5 }
            }}
          >
            <IconButton
              onClick={handleSwapUnits}
              sx={{
                p: 1.5,
                border: `1px solid ${theme.palette.divider}`,
                borderRadius: '50%',
                backgroundColor: theme.palette.background.paper,
                '&:hover': {
                  backgroundColor: theme.palette.action.hover
                },
                '&:active': {
                  backgroundColor: theme.palette.action.selected,
                  transform: 'scale(0.95)'
                }
              }}
            >
              <SwapVertIcon />
            </IconButton>
          </Grid>
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth>
              <InputLabel>{t('converter.toUnit')}</InputLabel>
              <Select
                value={toUnit}
                onChange={(e) => setToUnit(e.target.value)}
                label={t('converter.toUnit')}
                sx={{
                  '& .MuiSelect-select': {
                    py: 1.5
                  }
                }}
              >
                {availableUnits.map((unit) => (
                  <MenuItem key={unit} value={unit}>
                    {t(`units.${unit}`)}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12}>
            <Button
              fullWidth
              variant="contained"
              onClick={handleConvert}
              disabled={loading || !!error}
              sx={{
                py: 1.5,
                fontSize: '1rem',
                borderRadius: 1,
                textTransform: 'none',
                fontWeight: 500,
                '&:active': {
                  transform: 'scale(0.98)'
                }
              }}
            >
              {loading ? (
                <CircularProgress size={24} color="inherit" />
              ) : (
                t('converter.convert')
              )}
            </Button>
          </Grid>
        </Grid>
      </Paper>
      
      {conversionResult && (
        <ConversionDetails 
          result={conversionResult} 
          sx={{ 
            mt: 3,
            '@media (max-width: 600px)': {
              mx: 0
            }
          }} 
        />
      )}
      
      <CommonConversions
        onSelect={handleCommonConversionSelect}
        sx={{ 
          mt: 4,
          '@media (max-width: 600px)': {
            mx: 0
          }
        }}
      />
    </Box>
  );
};

export default ConverterForm;
