import React from 'react';
import { 
  Container, 
  Typography, 
  Box, 
  useTheme,
  Button,
  Grid
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useLanguage } from '../contexts/LanguageContext';
import GradientBackground from '../components/common/GradientBackground';
import Card from '../components/common/Card';
import CategoryGrid from '../components/common/CategoryGrid';
import Icon, { ICONS } from '../components/common/Icon';

const FEATURED_CONVERSIONS = [
  {
    titleKey: 'categories.length',
    descriptionKey: 'home.featured.length',
    icon: ICONS.LENGTH,
    path: '/convert/length',
    gradient: ['#3b82f633', '#2563eb33']
  },
  {
    titleKey: 'categories.weight',
    descriptionKey: 'home.featured.weight',
    icon: ICONS.WEIGHT,
    path: '/convert/weight',
    gradient: ['#4f46e533', '#6366f133']
  },
  {
    titleKey: 'categories.temperature',
    descriptionKey: 'home.featured.temperature',
    icon: ICONS.TEMPERATURE,
    path: '/convert/temperature',
    gradient: ['#db277833', '#ef444433']
  }
];

const ALL_CATEGORIES = [
  'length', 'weight', 'volume', 'temperature', 
  'area', 'speed', 'time', 'pressure',
  'energy', 'power', 'dataStorage', 'fuelEconomy'
];

const HomePage = () => {
  const theme = useTheme();
  const navigate = useNavigate();
  const { t } = useLanguage();

  return (
    <Box>
      <GradientBackground
        variant="hero"
        sx={{
          minHeight: '60vh',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          textAlign: 'center',
          py: 8
        }}
      >
        <Container maxWidth="lg">
          <Typography 
            variant="h1" 
            component="h1"
            sx={{ 
              mb: 3,
              fontSize: { xs: '2.5rem', sm: '3.5rem', md: '4rem' },
              fontWeight: 700
            }}
          >
            {t('home.hero.title')}
          </Typography>
          <Typography 
            variant="h2"
            component="p"
            sx={{ 
              mb: 4,
              fontSize: { xs: '1.25rem', sm: '1.5rem' },
              fontWeight: 400,
              opacity: 0.9
            }}
          >
            {t('home.hero.subtitle')}
          </Typography>
          <Button
            variant="contained"
            size="large"
            onClick={() => navigate('/convert/length')}
            startIcon={<Icon name={ICONS.CONVERT} />}
            sx={{ px: 4, py: 1.5 }}
          >
            {t('common.convert')}
          </Button>
        </Container>
      </GradientBackground>

      <Container maxWidth="lg" sx={{ py: 8 }}>
        <Typography 
          variant="h2" 
          component="h2"
          sx={{ 
            mb: 4,
            textAlign: 'center',
            fontSize: { xs: '2rem', sm: '2.5rem' },
            fontWeight: 600
          }}
        >
          {t('home.featured.title')}
        </Typography>

        <Grid container spacing={4} justifyContent="center">
          {FEATURED_CONVERSIONS.map((conversion) => (
            <Grid item xs={12} sm={6} md={4} key={conversion.path}>
              <Card
                variant="gradient"
                gradient={conversion.gradient}
                onClick={() => navigate(conversion.path)}
                sx={{ height: '100%' }}
              >
                <Box sx={{ p: 3, height: '100%', display: 'flex', flexDirection: 'column' }}>
                  <Icon 
                    name={conversion.icon}
                    size={40}
                    sx={{ mb: 2 }}
                  />
                  <Typography variant="h6" component="h3" sx={{ mb: 1, fontWeight: 600 }}>
                    {t(conversion.titleKey)}
                  </Typography>
                  <Typography variant="body1" sx={{ opacity: 0.8 }}>
                    {t(conversion.descriptionKey)}
                  </Typography>
                </Box>
              </Card>
            </Grid>
          ))}
        </Grid>

        <Box sx={{ mt: 8 }}>
          <Typography 
            variant="h2" 
            component="h2"
            sx={{ 
              mb: 4,
              textAlign: 'center',
              fontSize: { xs: '2rem', sm: '2.5rem' },
              fontWeight: 600
            }}
          >
            {t('home.allCategories.title')}
          </Typography>
          <CategoryGrid categories={ALL_CATEGORIES} />
        </Box>
      </Container>
    </Box>
  );
};

export default HomePage;
