import React from 'react';
import { Paper, Typography, Box, Divider, useTheme } from '@mui/material';
import Icon, { ICONS } from '../common/Icon';

const ConversionDetails = ({ result }) => {
  const theme = useTheme();

  // Early return if result is not provided or is invalid
  if (!result || typeof result !== 'object') return null;

  // Ensure from and to objects exist with default values
  const from = result.from || {};
  const to = result.to || {};

  // Create formatted strings with proper null checks
  const fromFormatted = from.formatted || `${from.value || ''} ${from.unit || ''}`.trim();
  const toFormatted = to.formatted || `${to.value || ''} ${to.unit || ''}`.trim();

  // If we don't have any values to display, don't render anything
  if (!fromFormatted && !toFormatted) return null;

  const resultBoxStyles = {
    bgcolor: theme.palette.mode === 'dark' ? 'rgba(37, 99, 235, 0.1)' : 'rgba(37, 99, 235, 0.05)',
    p: 4,
    borderRadius: 2,
    border: 1,
    borderColor: theme.palette.mode === 'dark' ? 'rgba(37, 99, 235, 0.2)' : 'rgba(37, 99, 235, 0.1)',
    boxShadow: theme.palette.mode === 'dark' 
      ? '0 4px 12px rgba(37, 99, 235, 0.1)' 
      : '0 4px 12px rgba(37, 99, 235, 0.05)',
  };

  return (
    <Paper 
      elevation={0}
      sx={{ 
        mt: 4, 
        p: 4,
        bgcolor: theme.palette.mode === 'dark' ? 'rgba(37, 99, 235, 0.05)' : 'rgba(37, 99, 235, 0.02)',
        borderRadius: 3,
        border: 1,
        borderColor: theme.palette.mode === 'dark' ? 'rgba(37, 99, 235, 0.1)' : 'rgba(37, 99, 235, 0.05)',
      }}
    >
      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 4 }}>
        {/* Conversion Result */}
        <Box>
          <Typography 
            variant="h6" 
            sx={{ 
              color: 'text.primary', 
              mb: 2,
              display: 'flex',
              alignItems: 'center',
              gap: 1,
              fontWeight: 600
            }}
          >
            <Icon name={ICONS.CONVERT} size={24} color="primary" />
            Conversion Result
          </Typography>
          <Box sx={resultBoxStyles}>
            <Box sx={{ 
              display: 'grid', 
              gridTemplateColumns: { xs: '1fr', md: '1fr auto 1fr' },
              gap: 4,
              textAlign: 'center'
            }}>
              <Box>
                <Typography 
                  variant="body2" 
                  sx={{ 
                    color: theme.palette.mode === 'dark' ? 'rgba(255, 255, 255, 0.7)' : 'rgba(0, 0, 0, 0.6)',
                    mb: 1,
                    fontWeight: 500
                  }}
                >
                  From
                </Typography>
                <Typography 
                  variant="h5"
                  sx={{ 
                    fontWeight: 600,
                    color: theme.palette.mode === 'dark' ? '#fff' : '#000'
                  }}
                >
                  {fromFormatted || 'N/A'}
                </Typography>
              </Box>
              <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                <Icon 
                  name={ICONS.ARROW_RIGHT} 
                  size={24} 
                  sx={{ 
                    color: theme.palette.mode === 'dark' ? 'rgba(255, 255, 255, 0.5)' : 'rgba(0, 0, 0, 0.3)'
                  }} 
                />
              </Box>
              <Box>
                <Typography 
                  variant="body2" 
                  sx={{ 
                    color: theme.palette.mode === 'dark' ? 'rgba(255, 255, 255, 0.7)' : 'rgba(0, 0, 0, 0.6)',
                    mb: 1,
                    fontWeight: 500
                  }}
                >
                  To
                </Typography>
                <Typography 
                  variant="h5"
                  sx={{ 
                    fontWeight: 600,
                    color: theme.palette.primary.main
                  }}
                >
                  {toFormatted || 'N/A'}
                </Typography>
              </Box>
            </Box>
          </Box>
        </Box>

        {/* Only show formula section if formula exists */}
        {result.formula && (
          <>
            <Divider sx={{ opacity: 0.1 }} />
            <Box>
              <Typography 
                variant="h6" 
                sx={{ 
                  color: 'text.primary', 
                  mb: 2,
                  display: 'flex',
                  alignItems: 'center',
                  gap: 1,
                  fontWeight: 600
                }}
              >
                <Icon name={ICONS.FORMULA} size={24} color="primary" />
                Conversion Formula
              </Typography>
              <Box sx={resultBoxStyles}>
                <Typography 
                  variant="body1" 
                  sx={{ 
                    fontFamily: 'monospace',
                    fontSize: '1.1rem',
                    color: theme.palette.mode === 'dark' ? '#fff' : '#000'
                  }}
                >
                  {result.formula}
                </Typography>
              </Box>
            </Box>
          </>
        )}

        {/* Only show steps if they exist */}
        {result.steps?.length > 0 && (
          <>
            <Divider sx={{ opacity: 0.1 }} />
            <Box>
              <Typography 
                variant="h6" 
                sx={{ 
                  color: 'text.primary', 
                  mb: 2,
                  display: 'flex',
                  alignItems: 'center',
                  gap: 1,
                  fontWeight: 600
                }}
              >
                <Icon name={ICONS.STEPS} size={24} color="primary" />
                Step by Step
              </Typography>
              <Box sx={resultBoxStyles}>
                {result.steps.map((step, index) => (
                  <Box key={index} sx={{ mb: index < result.steps.length - 1 ? 3 : 0 }}>
                    <Typography 
                      variant="subtitle2" 
                      sx={{ 
                        color: theme.palette.mode === 'dark' ? 'rgba(255, 255, 255, 0.7)' : 'rgba(0, 0, 0, 0.6)',
                        mb: 0.5,
                        fontWeight: 500,
                        display: 'flex',
                        alignItems: 'center',
                        gap: 1
                      }}
                    >
                      <Icon name={ICONS.STEP} size={20} color="primary" />
                      Step {index + 1}:
                    </Typography>
                    <Typography 
                      variant="body1" 
                      sx={{ 
                        fontFamily: 'monospace',
                        fontSize: '1.1rem',
                        color: theme.palette.mode === 'dark' ? '#fff' : '#000',
                        pl: 3.5
                      }}
                    >
                      {step}
                    </Typography>
                  </Box>
                ))}
              </Box>
            </Box>
          </>
        )}

        {/* Only show scientific notation if the value is very large or small */}
        {to.value && (Math.abs(to.value) > 1e9 || Math.abs(to.value) < 1e-9) && (
          <>
            <Divider sx={{ opacity: 0.1 }} />
            <Box>
              <Typography 
                variant="h6" 
                sx={{ 
                  color: 'text.primary', 
                  mb: 2,
                  display: 'flex',
                  alignItems: 'center',
                  gap: 1,
                  fontWeight: 600
                }}
              >
                <Icon name={ICONS.SCIENTIFIC} size={24} color="primary" />
                Scientific Notation
              </Typography>
              <Box sx={resultBoxStyles}>
                <Typography 
                  variant="body1" 
                  sx={{ 
                    fontFamily: 'monospace',
                    fontSize: '1.1rem',
                    color: theme.palette.mode === 'dark' ? '#fff' : '#000'
                  }}
                >
                  {to.value.toExponential(6)}
                </Typography>
              </Box>
            </Box>
          </>
        )}
      </Box>
    </Paper>
  );
};

export default ConversionDetails;
